/-- Even and odd subsets of ‚Ñï. -/
def evens : Set ‚Ñï := { n | n % 2 = 0 }
def odds  : Set ‚Ñï := { n | n % 2 = 1 }

/-- Counterexample: for X = ‚Ñï, odds ‚àà T_infty and (evens \ {0}) ‚àà T_infty, but their union is ‚Ñï \ {0}, whose complement {0} is neither infinite, empty, nor all of ‚Ñï; hence T_infty is not closed under unions. -/
theorem Munkres_exercise_13_3b :
  ‚àÉ (A B : Set ‚Ñï), A ‚àà T_infty (Set.univ : Set ‚Ñï) ‚àß B ‚àà T_infty (Set.univ : Set ‚Ñï) ‚àß (A ‚à™ B) ‚àâ T_infty (Set.univ : Set ‚Ñï) := by sorry

import Mathlib

open Set

/-- A simple predicate saying a collection of subsets is a topology. -/
def is_topology_on {X : Type _} (T : Set (Set X)) : Prop :=
  (‚àÖ : Set X) ‚àà T ‚àß (Set.univ : Set X) ‚àà T ‚àß
    (‚àÄ s t, s ‚àà T ‚Üí t ‚àà T ‚Üí s ‚à© t ‚àà T) ‚àß
    (‚àÄ S : Set (Set X), (‚àÄ s ‚àà S, s ‚àà T) ‚Üí (‚ãÉ‚ÇÄ S) ‚àà T)

/-- A concrete three-point type. -/
inductive X : Type
| a | b | c
open X

/-- Some subsets of X. -/
def s_a : Set X := {a}
def s_b : Set X := {b}
def s_c : Set X := {c}
def s_ab : Set X := {a, b}
def s_bc : Set X := {b, c}

/-- Two topologies on X. -/
def T1 : Set (Set X) := { s | s = (‚àÖ : Set X) ‚à® s = (Set.univ : Set X) ‚à® s = s_a ‚à® s = s_ab }

def T2 : Set (Set X) := { s | s = (‚àÖ : Set X) ‚à® s = (Set.univ : Set X) ‚à® s = s_c ‚à® s = s_bc }

/-- The union T1 ‚à™ T2 contains {a,b} and {b,c} but not {b}, so it is not closed under finite
    intersections; we state this as a theorem (proof omitted). -/
theorem union_of_topologies_not_topology :
  is_topology_on T1 ‚àß is_topology_on T2 ‚àß s_ab ‚àà (T1 ‚à™ T2) ‚àß s_bc ‚àà (T1 ‚à™ T2) ‚àß ¬¨ (s_b ‚àà (T1 ‚à™ T2)) := by
  sorry

