import Mathlib

/-- A statement: For a closed rectangle R in R^n, the outer measure m_star of R equals its volume. -/

variable {n : ℕ}

def rectangle (a b : Fin n → ℝ) : Set (Fin n → ℝ) := { x | ∀ i, a i ≤ x i ∧ x i ≤ b i }

def volume (a b : Fin n → ℝ) : ℝ := ∏ i, (b i - a i)

theorem outer_measure_closed_rectangle (m_star : Set (Fin n → ℝ) → ℝ) (a b : Fin n → ℝ) (hab : ∀ i, a i ≤ b i) :
  m_star (rectangle a b) = volume a b := by sorry
