import Mathlib
open Matrix

/-- The reflection matrix across the line through the origin making angle `Œ∏` with the x-axis.
    In coordinates (with respect to the standard basis) this is
    [[cos (2Œ∏), sin (2Œ∏)], [sin (2Œ∏), -cos (2Œ∏)]]. -/
def HŒ∏ (Œ∏ : ‚Ñù) : Matrix (Fin 2) (Fin 2) ‚Ñù :=
  Matrix.of fun i j =>
    if i = 0 ‚àß j = 0 then Real.cos (2 * Œ∏)
    else if i = 0 ‚àß j = 1 then Real.sin (2 * Œ∏)
    else if i = 1 ‚àß j = 0 then Real.sin (2 * Œ∏)
    else -Real.cos (2 * Œ∏)

/-- For each `Œ∏ : ‚Ñù`, the reflection matrix `HŒ∏ Œ∏` is orthogonal and has determinant `-1`.
    (This expresses that the reflection is a linear operator and an improper orthogonal matrix.) -/
theorem reflection_matrix_improper_orthogonal (Œ∏ : ‚Ñù) :
  Matrix.transpose (HŒ∏ Œ∏) * HŒ∏ Œ∏ = 1 ‚àß Matrix.det (HŒ∏ Œ∏) = -1 := by sorry

import Mathlib

open Real

/-- Rotation matrix R_Œ∏ in SO(2) as a 2√ó2 real matrix. -/
def R (Œ∏ : ‚Ñù) : Matrix (Fin 2) (Fin 2) ‚Ñù :=
  Matrix.ofFun fun i j =>
    if i = 0 ‚àß j = 0 then Real.cos Œ∏
    else if i = 0 ‚àß j = 1 then -Real.sin Œ∏
    else if i = 1 ‚àß j = 0 then Real.sin Œ∏
    else Real.cos Œ∏

/-- Reflection across the line through the origin that makes angle Œ∏ with the x-axis. -/
def H (Œ∏ : ‚Ñù) : Matrix (Fin 2) (Fin 2) ‚Ñù :=
  Matrix.ofFun fun i j =>
    if i = 0 ‚àß j = 0 then Real.cos (2 * Œ∏)
    else if i = 0 ‚àß j = 1 then Real.sin (2 * Œ∏)
    else if i = 1 ‚àß j = 0 then Real.sin (2 * Œ∏)
    else -Real.cos (2 * Œ∏)

/-- The orthogonal group O(2) (as 2√ó2 real orthogonal matrices) equals the union of the
    rotations R_Œ∏ and the reflections H_Œ∏. -/
def O2 : Set (Matrix (Fin 2) (Fin 2) ‚Ñù) := {A | Matrix.transpose A * A = 1}

theorem O2_description : O2 = (Set.range R) ‚à™ (Set.range H) := by sorry

import Mathlib

open Matrix

/-- The real 2√ó2 orthogonal group as a subgroup of the units of 2√ó2 real matrices. -/
def O2 : Set (Units (Matrix (Fin 2) (Fin 2) ‚Ñù)) :=
  { U | U.val * U.val.transpose = 1 }

/-- The special orthogonal group (rotations) inside O(2). -/
def SO2 : Set (Units (Matrix (Fin 2) (Fin 2) ‚Ñù)) :=
  { U | U.val * U.val.transpose = 1 ‚àß U.val.det = 1 }

/-- For any fixed improper orthogonal matrix A (determinant -1), O(2) is generated by SO(2) and A. -/
theorem O2_generated_by_SO2_and_A (A : Units (Matrix (Fin 2) (Fin 2) ‚Ñù))
  (hA_orth : A.val * A.val.transpose = 1) (hA_improper : A.val.det = -1) :
  Subgroup.closure (SO2 ‚à™ {A}) = { U : Units (Matrix (Fin 2) (Fin 2) ‚Ñù) | U.val * U.val.transpose = 1 } := by sorry
