def rational_open_interval_basis : Set (Set â„) :=
  { I | âˆƒ a b : â„š, (a : â„) < (b : â„) âˆ§ I = Ioo (a : â„) (b : â„) }

/-- The usual lower limit basis consisting of half-open intervals [a,b). -/
def lower_limit_basis : Set (Set â„) :=
