import Mathlib

open Set

variable {n : ℕ}

-- ambient space R^n
def Rn := Fin n → ℝ

-- abstract predicate for open rectangles and a volume function on them
variable (is_open_rectangle : Set Rn → Prop)
variable (V : Set Rn → ENNReal)

def mstar (S : Set Rn) : ENNReal :=
  Inf { t | ∃ (R : ℕ → Set Rn), (∀ i, is_open_rectangle (R i)) ∧ S ⊆ ⋃ i, R i ∧ t = ∑' fun i => V (R i) }

def mustar (S : Set Rn) : ENNReal :=
  Inf { t | ∃ (I : Type) [Countable I] (R : I → Set Rn), (∀ i, is_open_rectangle (R i)) ∧ S ⊆ ⋃ i, R i ∧ t = ∑' fun i => V (R i) }

theorem mustar_eq_mstar (S : Set Rn) : mustar is_open_rectangle V S = mstar is_open_rectangle V S := by sorry
