import Mathlib

variable {R : Type*} [Semiring R] {n : Nat}

/-- The general linear group of n×n matrices over R. -/
def GL : Type* := Units (Matrix (Fin n) (Fin n) R)

/-- If G and H are matrix groups (subgroups of GL), then their intersection is a matrix group. -/
theorem matrix_groups_inter (G H : Subgroup GL) : Subgroup GL := by sorry

