import Mathlib

/-- Show that the product of any two reflection matrices in O(2) is a rotation matrix (i.e., an element of SO(2)). -/

def Reflection (A : Matrix (Fin 2) (Fin 2) ℝ) : Prop :=
  Aᵀ ⬝ A = 1 ∧ A.det = -1


def Rotation (A : Matrix (Fin 2) (Fin 2) ℝ) : Prop :=
  Aᵀ ⬝ A = 1 ∧ A.det = 1

theorem product_of_two_reflections_in_O2_is_in_SO2
  {A B : Matrix (Fin 2) (Fin 2) ℝ}
  (hA : Reflection A) (hB : Reflection B) : Rotation (A ⬝ B) := by
  sorry
